mapper.select:
SELECT c.cart_code cartCode, c.cart_quantity cartQuantity, c.h_item_code hItemCode, h.h_item_name hItemName, h.h_item_selling_price hItemSellingPrice, c.sub_code subCode FROM cart c inner join head_item h on c.h_item_code = h.h_item_code where sub_code = #{cartDetail.subCode}
order by cart_code desc
order by ${cartSearch.criteria} ${cartSearch.upDown}

mapper.update:
UPDATE 
   cart
SET
   cart_quantity= #{cartQuantity}
WHERE 
   cart_code = #{cartCode}
