mapper.select:
SELECT contract_code, contract_name, contract_regit_date, contract_actual_date, contract_expiry_date, contract_n, contract_expire_date, sub_code, re_contract_status FROM contract
sub_code = #{subCode}
AND date(contract_regit_date) >= #{contractSearch.regitDateStart} and date(contract_regit_date) <= #{contractSearch.regitDateEnd}
AND ${contractSearch.searchKey} like CONCAT('%',#{contractSearch.searchContract},'%')
contract_regit_date
${contractSearch.criteria} ${contractSearch.upDown}

mapper.insert:
	
		select if(isnull(contract_code),'contract_code1', concat('contract_code',MAX(CONVERT(SUBSTRING(contract_code,14), UNSIGNED))+1))
		as contractCode
		from contract
		
		INSERT INTO contract
		(contract_code
		,contract_name
		,contract_regit_date
		,contract_actual_date
		,contract_expiry_date
		,contract_deposit
		,contract_profit_percent
		,contract_n
		,head_staff_id
		,sub_code
		)
		
		VALUES(
		#{contractCode}
		,#{contractName}
		,NOW()
		,#{contractActualDate}
		,#{contractExpiryDate}
		,#{contractDeposit}
		,#{contractProfitPercent}
		,#{contractN}
		,#{headStaffId}
		,#{subCode}
		)


	select if(isnull(contract_file_code),'contract_file_code1', concat('contract_file_code',MAX(CONVERT(SUBSTRING(contract_file_code,19), UNSIGNED))+1))
	as contractFileCode
	from contract_file

INSERT INTO contract_file
(contract_file_code
,contract_code
,contract_file
,file_name
,random_name
)
VALUES
(#{contractFileCode}
,#{contractCode}
,#{contractFile}
,#{fileName}
,#{randomName}
)

	
		select if(isnull(contract_code),'contract_code1', concat('contract_code',MAX(CONVERT(SUBSTRING(contract_code,14), UNSIGNED))+1))
