mapper.select:
select orders_code, sub_orders_group, sub_orders_quantity, sub_orders_date, sub_orders_head_check, sub_orders_status, total_account_group, orders_pay, orders_pay_date, head_item_code, sub_code, sub_staff_code, head_staff_id, head_orders_confirm from sub_orders
sub_code = #{subLogin.subCode}
AND date(sub_orders_date) >= #{ordersSearch.regitDateStart} and date(sub_orders_date) <= #{ordersSearch.regitDateEnd}
order by sub_orders_date desc
order by ${ordersSearch.criteria} ${ordersSearch.upDown}
