mapper.select:
SELECT sub_sell_code ,menu_code ,sub_sell_group ,sub_sell_date ,sub_sell_practical_selling_price ,total_account_group ,sub_sell_final ,sub_sell_final_date ,pay_method ,sub_code ,event_code ,sub_staff_code ,sub_sell_final_staff ,sub_sell_cost FROM sub_sell
sub_code = #{subCode}
AND date(sub_sell_date) >= #{search.regitDateStart} and date(sub_sell_date) <= #{search.regitDateEnd}
AND ${search.searchKey} like CONCAT('%',#{search.search},'%')
order by sub_sell_date desc
order by ${search.criteria} ${search.upDown}
LIMIT 0,${search.viewMore}

mapper.insert:

	select if(isnull(sub_account_code),'sub_account_code1', concat('sub_account_code', MAX(CONVERT(SUBSTRING(sub_account_code,17), UNSIGNED))+1)) 
	as subAccountCode
	from sub_account



INSERT INTO sub_account
(sub_account_code, 
sub_account_flow, 
total_account_group, 
sub_account_price,
sub_account_request_date, 
sub_account_detail, 
subject_code, 
sub_code 
)

