mapper.select:
SELECT 
	sub_staff_code,sub_staff_name,sub_code, sub_staff_level,sub_staff_join,sub_staff_resign,head_staff_id,sub_staff_permit_date,sub_staff_regit_date,sub_staff_salary,total_account_group      
FROM 
	sub_staff

	sub_code = #{subLogin.subCode}

		AND date(sub_staff_regit_date) >= #{subStaffSearch.regitDateStart} and date(sub_staff_regit_date) <= #{subStaffSearch.regitDateEnd}  


			AND  ${subStaffSearch.searchKey} like CONCAT('%',#{subStaffSearch.searchSubStaff},'%') 


		AND sub_staff_resign is null


		AND sub_staff_resign is not null




	order by sub_staff_regit_date desc


	order by ${subStaffSearch.criteria} ${subStaffSearch.upDown}

LIMIT 0,${subStaffSearch.viewMore}

mapper.update:
UPDATE sub_staff
SET head_staff_id = #{headStaffId},sub_staff_permit_date = NOW()
WHERE sub_staff_code = #{subStaffCode}
